import React, { useState } from "react";
import "./App.css";
import tickImg from "./check.png";

function App() {
  const genderList = ["Male", "Female", "Others", "Prefer Not to Say"];

  const [selectedGender, setSelectedGender] = useState("");

  const genderHandler = (gender) => {
    setSelectedGender(gender);
  };

  return (
    <div className="App">
      <div className="selectGender">
        <h1>What's your Gender?</h1>
        <div className="genderOptions">
          {" "}
          {genderList.map((item) => {
            return (
              <button
                className="btn"
                onClick={() => {
                  genderHandler(item);
                }}
              >
                {item}
                {selectedGender === item && (
                  <img src={tickImg} alt="tick"></img>
                )}
              </button>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
