import React, { useState, useEffect } from "react";
import trashImg from "./trash.png";
import deleteImg from "./delete.png";

import "./InputBox.css";
import Numbox from "./Numbox";

const InputBox = ({ ticketList, setTicketList }) => {
  const [error, setError] = useState([]);
  const [ticket, setTicket] = useState("");

  const validateInput = () => {
    setError([]);

    if (ticketList.length >= 5)
      setError((err) => [...err, "Ticket Limit Reached"]);

    if (parseInt(ticket) < 100000 || parseInt(ticket) > 999999)
      setError((err) => {
        return [...err, "Ticket number must be 6 digits"];
      });
    if (ticketList.includes(parseInt(ticket)))
      setError((err) => {
        return [
          ...err,
          "Token List contains same Token. Token must be unique.",
        ];
      });
  };
  const addTicketHandler = () => {
    setTicketList((tList) => {
      return [...tList, parseInt(ticket)];
    });
    setTicket("");
  };

  const generateToken = () => {
    function getRandomArbitrary(min, max) {
      return Math.random() * (max - min) + min;
    }
    let x;
    do {
      x = Math.floor(getRandomArbitrary(100000, 999999));
    } while (ticketList.includes(x.toString()));

    setTicket(x);
  };

  useEffect(() => {
    let inputTimeout = setTimeout(() => {
      validateInput();
    }, 500);
  }, [ticket]);

  return (
    <div className="inputbox">
      <div className="inputpad">
        <div className="inputrow">
          <div className="searchrow">
            <input
              onChange={(e) => {
                setTicket(e.target.value);
              }}
              onKeyDown={(e) => {
                e.key === "Enter" && addTicketHandler();
              }}
              value={ticket}
            />
          </div>
          <div className="numrow">
            <Numbox
              number={7}
              type="square"
              ticket={ticket}
              setTicket={setTicket}
            ></Numbox>
            <Numbox
              number={8}
              type="square"
              ticket={ticket}
              setTicket={setTicket}
            ></Numbox>
            <Numbox
              number={9}
              type="square"
              ticket={ticket}
              setTicket={setTicket}
            ></Numbox>
          </div>

          <div className="numrow">
            <Numbox
              number={6}
              type="square"
              ticket={ticket}
              setTicket={setTicket}
            ></Numbox>
            <Numbox
              number={5}
              type="square"
              ticket={ticket}
              setTicket={setTicket}
            ></Numbox>
            <Numbox
              number={4}
              type="square"
              ticket={ticket}
              setTicket={setTicket}
            ></Numbox>
          </div>
          <div className="numrow">
            <Numbox
              number={3}
              type="square"
              ticket={ticket}
              setTicket={setTicket}
            ></Numbox>
            <Numbox
              number={2}
              type="square"
              ticket={ticket}
              setTicket={setTicket}
            ></Numbox>
            <Numbox
              number={1}
              type="square"
              ticket={ticket}
              setTicket={setTicket}
            ></Numbox>
          </div>
          <div className="numrow">
            <button
              className="numpad-icon"
              onClick={() => {
                setTicket((num) => {
                  if (ticket === "") {
                    return "";
                  }

                  let temp = num / 10;
                  return Math.floor(temp);
                });
              }}
            >
              <img src={deleteImg} />
            </button>
            <Numbox number={0}></Numbox>
            <button
              className="numpad-icon"
              onClick={() => {
                setTicket("");
              }}
            >
              <img src={trashImg} />
            </button>
          </div>
        </div>
      </div>
      <div></div>
    </div>
  );
};

export default InputBox;
