import React from "react";

import "./InputBox.css";
import Numbox from "./Numbox";

const InputBox = ({ ticketList, setTicketList }) => {
  const [error, setError] = useState([]);
  const [ticket, setTicket] = useState("");

  const validateInput = () => {
    setError([]);

    if (ticketList.length >= 5)
      setError((err) => [...err, "Ticket Limit Reached"]);

    if (parseInt(ticket) < 100000 || parseInt(ticket) > 999999)
      setError((err) => {
        return [...err, "Ticket number must be 6 digits"];
      });
    if (ticketList.includes(parseInt(ticket)))
      setError((err) => {
        return [
          ...err,
          "Token List contains same Token. Token must be unique.",
        ];
      });
  };
  const addTicketHandler = () => {
    setTicketList((tList) => {
      return [...tList, parseInt(ticket)];
    });
    setTicket("");
  };

  const generateToken = () => {
    function getRandomArbitrary(min, max) {
      return Math.random() * (max - min) + min;
    }
    let x;
    do {
      x = Math.floor(getRandomArbitrary(100000, 999999));
    } while (ticketList.includes(x.toString()));

    setTicket(x);
  };

  useEffect(() => {
    let inputTimeout = setTimeout(() => {
      validateInput();
    }, 500);
  }, [ticket]);

  return (
    <div className="inputbox">
      <div className="inputpad">
        <div className="inputrow">
          <div className="searchbox">
            <input></input>
          </div>
          <div className="numrow">
            <Numbox number={7}></Numbox>
            <Numbox number={8}></Numbox>
            <Numbox number={9}></Numbox>
          </div>

          <div className="numrow">
            <Numbox number={6}></Numbox>
            <Numbox number={5}></Numbox>
            <Numbox number={4}></Numbox>
          </div>
          <div className="numrow">
            <Numbox number={3}></Numbox>
            <Numbox number={2}></Numbox>
            <Numbox number={1}></Numbox>
          </div>
        </div>
      </div>
      <div></div>
    </div>
  );
};

export default InputBox;
