import React, { useEffect, useState } from "react";
import "./DisplayItem.css";
import deleteImg from "./trash.png";

const DIsplayItem = ({ index, ticket, setTicketList, ticketList }) => {
  const getdigits = (num) => {
    let li = [];
    let temp = num;
    while (temp > 0) {
      li = [temp % 10, ...li];
      temp = Math.floor(temp / 10);
    }
    return li;
  };

  const [liDigits, setLiDigits] = useState([]);
  useEffect(() => {
    setLiDigits(ticket);
  }, [ticketList]);

  return (
    <div className="ticketOuter">
      <h2>Ticket #{index}</h2>
      <div className="digitflex">
        {liDigits.map((digit, index) => {
          return (
            <p className="digit" key={index}>
              <span>{digit}</span>
            </p>
          );
        })}
      </div>
      <button
        className="deleteinDisplay"
        onClick={setTicketList((item) => {
          const index = item.indexOf(ticket);
          let temp = item;
          temp.splice(index, 1);
          return temp;
        })}
      >
        <img src={deleteImg} />
      </button>
    </div>
  );
};
export default DIsplayItem;
